import React, { useState } from "react";
import { useLocation } from "react-router-dom";


const Nivelamento = () => {
  const location = useLocation();
  const competencias = location.state?.competencias || [];

  // Adicione um estado para armazenar os níveis selecionados
  const [nivelSelecionado, setNivelSelecionado] = useState({});

  // Função para atualizar o nível selecionado
  const handleNivelChange = (index, novoNivel) => {
    setNivelSelecionado((prev) => ({ ...prev, [index]: novoNivel }));
  };

  return (
    <div>
      <div style={{textAlign:'center', marginTop:'25px'}}>
        <h2>Agora para finalizar nos informe em que nível você se encontra em cada tecnologia selecionada:</h2>
      </div>
      
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent:'center', marginTop:'50px' }}>
        {competencias.map((competencia, index) => (
          <div key={index} style={{ margin: '10px', textAlign: 'center',display:'flex', flexWrap:'wrap', justifyContent:'center', width:'17%',
          
          position: 'relative',
          display: 'inline-block',
          margin: '5px',
          boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px',
          borderRadius: '16px',
          
          }}>
            <img
              src={competencia.imagem}
              alt={`Imagem de ${competencia.valor}`}
              style={{ width: '150px', height: '150px', display: 'block'}}
            />
           
          </div>

          
        ))}

<select
              id="selectValue"
              className="form-select form-select-lg mb-3"
              aria-label=".form-select-lg example"
              value={nivelSelecionado[index] || ''}
              onChange={(e) => handleNivelChange(index, e.target.value)}
              style={{ marginTop: '10px' }}
            >
              <option value="" data-default disabled selected>Selecione um Nível...</option>
              <option value="Master">Master</option>
              <option value="Leader">Leader</option>
              <option value="Teacher">Teacher</option>
              <option value="Professional">Professional</option>
              <option value="Practitioner">Practitioner</option>
              <option value="Apprentice">Apprentice</option>
              <option value="Beginner">Beginner</option>
            </select>

        
      </div>
    </div>
  );
};

export default Nivelamento;
