import styled from 'styled-components';

export const AreaHeader = styled.div`

* {
    margin: 0;
    padding: 0;
    
}

.logo img {
    width: 45px;
    height: 43.875px;
    flex-shrink: 0;
    border-radius: 45px;
    background: url(liferayLogo.png), #F0F2F5 50% / cover no-repeat;
    
}

.search-box {
    width: 431px;
    height: 41px;
    flex-shrink: 0;
    border-radius: 10px;
    background: #fff;
}

.search-btn {
    margin-left: 15px;
    border:none;
    border-radius:0 7px 7px 0;
    width: 15.311px;
    height: 15.311px;
    flex-shrink: 0;
    font-weight:bold;
    cursor: pointer;
}

.search-text {
    border: none;
    outline: none;
    width: 350px;
    margin-left: 10px;
    font-family: Poppins;
    font-size: 20px;
    font-style: normal;
    font-weight: 400;
    line-height: 40px;
    letter-spacing: 0.265px; 

}

.search-arrow {
    margin-left: 0;
    width: 24px;
    height: 24px;
    flex-shrink: 0;
    cursor: pointer;
}

a {
    color: #fff;
    text-decoration: none;
}

a:hover {
    opacity: 0.7;
}

.logo {
    font-size: 24px;
    text-transform: uppercase;
    letter-spacing: 4px;
}

nav {
    display: flex;
    justify-content: space-around;
    align-items: center;
    background: #0B63CE;
    height: 61px;
    color: #F0F2F5;
    font-family:'Inter', sans-serif;
    font-size: 15px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
    letter-spacing: 0.24px;
}

.nav-list {
    list-style: none;
    display: flex;
}

.nav-list li {
    background: #0B63CE;
    letter-spacing: 3px;
    margin-left: 32px;
}

.my-events{
    color: #191F28;
    font-family:'Inter', sans-serif;
    font-size: 30px;
    font-style: normal;
    font-weight: 400;
    line-height: normal;
    letter-spacing: 0.4px;
    margin-top: 59px;
    margin-left: 199px;
}

.events-java {
    width: 449px;
    height: 304px;
    flex-shrink: 0;
    border-radius: 15px;
    margin-left: 174px;
    margin-top: 149px;
}

`;
