import styled from 'styled-components';

export const AreaHeader = styled.div`

/* Estilos para telas maiores que 600px */
.header-bar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 20px;
  background-color: #333; /* Cor de fundo desejada */
  color: #fff; /* Cor do texto desejada */
}

.nav-bar {
  display: flex;
  align-items: center;
}

.logo-img-search-box {
  display: flex;
  align-items: center;
}

.search-box {
  display: flex;
  align-items: center;
}

.nav-items-btn-user-img {
  display: flex;
  align-items: center;
}

.nav-list {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
}

.nav-item {
  margin-right: 20px;
}

.nav-link {
  text-decoration: none;
  color: #fff;
}

.user-img {
  margin-left: 20px;
}

/* Estilos para telas menores ou iguais a 600px */
@media (max-width: 600px) {
  .nav-bar {
    flex-direction: column;
  }

  .search-box {
    margin-top: 10px;
  }

  .nav-list {
    flex-direction: column;
  }

  .nav-item {
    margin: 10px 0;
  }
}



`;
