import React, { useState } from "react";
import { Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import { ContainerPage, TittlePage } from "../../components/Main";

const Page = () => {
  const [competencias, setCompetencias] = useState([]);
  const [inputOutros, setInputOutros] = useState('');
  const [selectValue, setSelectValue] = useState('');

  const adicionarCompetencia = (valor) => {
    const novaCompetencia = {
      valor: valor,
      imagem: obterCaminhoImagem(valor),
    };

    setCompetencias([...competencias, novaCompetencia]);
  };

  const removerCompetencia = (index) => {
    const novasCompetencias = [...competencias];
    novasCompetencias.splice(index, 1);
    setCompetencias(novasCompetencias);
  };

  const handleAdicionarCompetencia = () => {
    let valorSelecionado = selectValue;

    if (selectValue === 'Outros') {
      valorSelecionado = inputOutros;
      setInputOutros(''); // Limpa o valor do input
    }

    adicionarCompetencia(valorSelecionado);
  };

  // Restante do seu código

  return (
    <ContainerPage>
      <main>
        <form id="form2">
          {/* Seção de competências */}
          <section className="competencias-usuario">
            <div className="competencias">
              <label id="select-label" htmlFor="selectValue">Escolha suas Competências:</label>
              <select
                id="selectValue"
                className="form-select form-select-lg mb-3"
                aria-label=".form-select-lg example"
                value={selectValue}
                onChange={(e) => setSelectValue(e.target.value)}
              >
                <option disabled value="">Competências...</option>
                <option value="Java">Java</option>
                <option value="Python">Python</option>
                <option value="JavaScript">JavaScript</option>
                <option value="React">React</option>
                <option value="Angular">Angular</option>
                <option value="TypeScript">TypeScript</option>
              </select>
            </div>
          </section>

          {/* Lista de competências adicionadas */}
          <div id="opcoes-container">
  <ul id="lista-competencias" style={{ listStyleType: 'none', padding: 0 }}>
    {competencias.map((competencia, index) => (
      <div
        key={index}
        style={{
          position: 'relative', // Adiciona posição relativa ao container
          display: 'inline-block',
          margin: '5px',
          
          borderRadius: '16px',
        }}
      >
        <li
          style={{
            color: 'white',
            padding: '5px',
            width: '200px',
            height: '200px',
            position: 'relative', // Adiciona posição relativa à competência
          }}
        >
          {competencia.valor}
          <img
            src={competencia.imagem}
            alt={`Imagem de ${competencia.valor}`}
            style={{ width: '150px', height: '150px', display: 'block', margin: '10px auto' }}
          />
          <button
            type="button"
            onClick={() => removerCompetencia(index)}
            style={{
              position: 'absolute', // Adiciona posição absoluta ao botão
              top: 0,
              right: 0,
              border: 'none',
              background: 'none',
              cursor: 'pointer',
            }}
          >
            <img src="../../../../multiplesign.png" alt="Remover" style={{ width: '15px', height: '15px' }} />
          </button>
        </li>
      </div>
    ))}
  </ul>
</div>

          {/* Botão para adicionar competência */}
          <div className="box-btn-add">
            <button type="button" onClick={handleAdicionarCompetencia}>
              Adicionar Competência
              <img src="../../../plus.png" id="img-plus" alt="Plus" />
            </button>
          </div>

          {/* Botões de navegação */}
          <div className="actions">
            <Link to="/"> <button type="button" className="buttons" id="btnNext1">Voltar<img src="../../../seta.png" alt="Seta" /></button></Link>
            <Link to="/Competencias"> <button type="submit" className="buttons" id="btnNext1">Avançar<img src="../../../seta.png" alt="Seta" /></button></Link>
          </div>
        </form>
      </main>
    </ContainerPage>
  );
}

// Função para obter o caminho da imagem com base na competência
const obterCaminhoImagem = (competencia) => {
  const imagensFundo = {
    Java: '../../../../javaimg.png',
    Python: '../../../../pythonimg.webp',
    JavaScript: '../../../../javascript.png',
    React: '../../../../react.png',
    Angular: '../../../../angularimg.png',
    TypeScript: '../../../../typescriptimg.png',
    Outros: 'outros-background.jpg',
  };

  return `/img/${imagensFundo[competencia] || 'default-background.jpg'}`;
}

export default Page;
