import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Select from "react-select";
import 'bootstrap/dist/css/bootstrap.min.css';

import { ContainerPage, TittlePage } from "../../components/Main";

const programmingLanguages = [
  { value: "Java", label: "Java" },
  { value: "Python", label: "Python" },
  { value: "JavaScript", label: "JavaScript" },
  // Outras opções...
];

const Page = () => {
  const [competencias, setCompetencias] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState(null);
  const [hoveredOption, setHoveredOption] = useState(null); // Adicione o estado para a opção no hover
  const navigate = useNavigate();

  const adicionarCompetencia = (valor) => {
    if (!competencias.some((c) => c.valor === valor)) {
      const novaCompetencia = {
        valor: valor,
        imagem: obterCaminhoImagem(valor),
      };
      setCompetencias([...competencias, novaCompetencia]);
    }
    setSelectedLanguage(null);
  };

  const removerCompetencia = (index) => {
    const novasCompetencias = [...competencias];
    novasCompetencias.splice(index, 1);
    setCompetencias(novasCompetencias);
  };

  return (
    <ContainerPage>
      <TittlePage>
        <div className="wc-text">
          <strong>Muito bem!</strong>
          <br />
          <p>
            Agora escolha <strong>5 competências</strong> técnicas que você
            possui.
            <br />
            Ah! Não se preocupe depois você poderá adicionar mais competências
            ao seu perfil ou removê-las na área de configurações.
          </p>
        </div>
      </TittlePage>
      <main>
        <form id="form2">
          {/* Seção de competências */}
          <section className="competencias-usuario">
            <div className="wrapper">
              <Select
                value={selectedLanguage}
                onChange={(selectedOption) => {
                  setSelectedLanguage(selectedOption);
                  adicionarCompetencia(selectedOption.label);
                }}
                options={programmingLanguages}
                placeholder="Digite ou selecione as tecnologias..."
              >
                {programmingLanguages.map((language) => (
                  <option
                    key={language.value}
                    onMouseOver={() => setHoveredOption(language.label)} // Atualize o estado no hover
                  >
                    {language.label}
                  </option>
                ))}
              </Select>
            </div>
          </section>

          {/* Campo de input para mostrar a opção no hover */}
          <input
            type="text"
            value={hoveredOption || ""}
            placeholder="Nome da competência"
            readOnly
          />

          {/* Lista de competências adicionadas */}
          <div id="opcoes-container">
            <ul
              id="lista-competencias"
              style={{ listStyleType: "none", padding: 0 }}
            >
              {competencias.map((competencia, index) => (
                <div
                  key={index}
                  style={{
                    position: "relative",
                    display: "inline-block",
                    margin: "5px",
                    boxShadow: "rgba(99, 99, 99, 0.2) 0px 2px 8px 0px",
                    borderRadius: "16px",
                  }}
                >
                  <li
                    style={{
                      color: "white",
                      padding: "5px",
                      width: "200px",
                      height: "200px",
                      position: "relative",
                      bottom: "5px",
                    }}
                  >
                    {competencia.valor}
                    <img
                      src={competencia.imagem}
                      alt={`Imagem de ${competencia.valor}`}
                      style={{
                        width: "150px",
                        height: "150px",
                        display: "block",
                        margin: "10px auto",
                      }}
                    />
                    <button
                      type="button"
                      onClick={() => removerCompetencia(index)}
                      style={{
                        position: "absolute",
                        top: 0,
                        right: 0,
                        border: "none",
                        background: "none",
                        cursor: "pointer",
                      }}
                    >
                      <img
                        src="../../../../multiplesign.png"
                        alt="Remover"
                        style={{ width: "15px", height: "15px" }}
                      />
                    </button>
                  </li>
                </div>
              ))}
            </ul>
          </div>

          <div className="box-btn-add">
            {/* Botão de adicionar competências removido */}
          </div>

          <div className="actions">
            <Link to="/">
              {" "}
              <button
                type="button"
                className="buttons"
                id="btnBack"
              >
                <img
                  src="../../../../setaesquerda.png"
                  alt="Seta"
                />
                Voltar
              </button>
            </Link>
            <button
              type="submit"
              className="buttons"
              id="btnNext"
              onClick={() => navigate("/Nivelamento", { state: { competencias } })}
            >
              Avançar
              <img
                src="../../../../setadireita.png"
                alt="Seta"
              />
            </button>
          </div>
        </form>
      </main>
    </ContainerPage>
  );
};
// Função para obter o caminho da imagem com base na competência
const obterCaminhoImagem = (competencia) => {
  const imagensFundo = {
    Java: "../../../../javaimg.png",
    Python: "../../../../pythonimg.webp",
    JavaScript: "../../../../javascript.png",
    React: "../../../../react.png",
    Angular: "../../../../angularimg.png",
    TypeScript: "../../../../typescriptimg.png",
    "C++": "../../../../c++img.png",
    "C#": "../../../../csharpimg.png",
    Ruby: "../../../../rubyimg.png",
  };

  return `/img/${imagensFundo[competencia] || "default-background.jpg"}`;
};

export default Page;
