import styled from "styled-components";

export const AreaFooter = styled.div`


    background-color: #0d2d67;
    margin-top: 30px;


footer .footer-info {
    display: flex;
    color: #FFF;
    padding-top: 100px;
    padding-left: 40px;
}

footer .current-language {
    display: flex;
    align-items: center;
    position: relative;
    height: 20%;
    bottom: 70px;
    left: 100px;
    gap: 10px;
    cursor: pointer;
}

footer .footer-info ul {
    width: 25%;
}

footer .footer-info h3 {
    margin-bottom: 16px;
    font-size: 0.833125em;
    font-weight: 400;
    letter-spacing: 1px;
    margin-bottom: 0;
    text-transform: uppercase;
}

footer .footer-info ul>p {
    margin-bottom: 40px;
}

footer .footer-info li {
    margin: 10px 0;
}

footer .footer-info a {
    color: #FFF;
}

footer .redes-sociais {
    display: flex;
    align-items: center;
    position: relative;
    bottom: 80px;
    gap: 20px;
}

footer .contact {
    margin-top: 20px;
}


footer .footer-info a:hover {
    color: grey;
}

footer .by-lf {
    display: flex;
    justify-content: space-evenly;
    align-items: center;
    padding-top: 150px;
    padding-bottom: 50px;
    color: #FFF;
}

footer .by-lf a {
    color: #FFF;
}

footer .by-lf a:hover {
    color: black;
}

.call-center {
    width: 50%;
    position: relative;
    bottom: 40px;
}


`;
