import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { ContainerPage } from "../../components/Main";

const Page = () => {
    const [competencias, setCompetencias] = useState([]);
    const [selectValue, setSelectValue] = useState('');
    const [isSelectOpen, setIsSelectOpen] = useState(false);
    const navigate = useNavigate();

    const adicionarCompetencia = (valor) => {
        const novaCompetencia = {
            valor: valor,
            imagem: obterCaminhoImagem(valor),
        };

        setCompetencias([...competencias, novaCompetencia]);
    };

    const removerCompetencia = (index) => {
        const novasCompetencias = [...competencias];
        novasCompetencias.splice(index, 1);
        setCompetencias(novasCompetencias);
    };

    const handleAdicionarCompetencia = () => {
        adicionarCompetencia(selectValue);
        setSelectValue('');
    };

    const competenciaOptions = [
        "Java",
        "Python",
        "JavaScript",
        "React",
        "Angular",
        "TypeScript"
    ];

    return (
        <ContainerPage>
            <main>
                <form id="form2">
                    {/* Seção de competências */}
                    <section className="competencias-usuario">
                        <div className="competencias">
                            <label id="select-label" htmlFor="selectValue">Escolha suas Competências:</label>
                            <div className="custom-select-container">
                                <input
                                    type="text"
                                    className="custom-select-search"
                                    placeholder="Pesquisar..."
                                    value={selectValue}
                                    onChange={(e) => setSelectValue(e.target.value)}
                                    onClick={() => setIsSelectOpen(true)}
                                />
                                <select
                                    id="selectValue"
                                    className="custom-select form-select form-select-lg mb-3"
                                    aria-label=".form-select-lg example"
                                    value={selectValue}
                                    onChange={(e) => setSelectValue(e.target.value)}
                                    onBlur={() => setIsSelectOpen(false)}
                                >
                                    <option value="" data-default disabled>Competências...</option>
                                    {competenciaOptions.map((option, index) => (
                                        <option key={index} value={option}>
                                            {option}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                    </section>

                    {/* Restante do código permanece inalterado */}
                    {/* ... */}
                </form>
            </main>
        </ContainerPage>
    );
}

// Função para obter o caminho da imagem com base na competência
const obterCaminhoImagem = (competencia) => {
    const imagensFundo = {
        Java: '../../../../javaimg.png',
        Python: '../../../../pythonimg.webp',
        JavaScript: '../../../../javascript.png',
        React: '../../../../react.png',
        Angular: '../../../../angularimg.png',
        TypeScript: '../../../../typescriptimg.png',
        Outros: 'outros-background.jpg',
    };

    return `/img/${imagensFundo[competencia] || 'default-background.jpg'}`;
}

export default Page;
