import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

import { ContainerPage } from "../../components/Main";

const Page = () => {
    const [competencias, setCompetencias] = useState([]);
    const [selectValue, setSelectValue] = useState('');
    const navigate = useNavigate();

    // Aqui, você pode adicionar o código JavaScript fornecido abaixo

    // Início do código JavaScript fornecido
    const wrapper = document.querySelector(".wrapper");
    const selectBtn = wrapper.querySelector(".select-btn");
    const searchInp = wrapper.querySelector("input");
    const options = wrapper.querySelector(".options");

    const countries = ["Afghanistan", "Algeria", "Argentina", "Australia", "Bangladesh", "Belgium", "Bhutan",
        "Brazil", "Canada", "China", "Denmark", "Ethiopia", "Finland", "France", "Germany",
        "Hungary", "Iceland", "India", "Indonesia", "Iran", "Italy", "Japan", "Malaysia",
        "Maldives", "Mexico", "Morocco", "Nepal", "Netherlands", "Nigeria", "Norway", "Pakistan",
        "Peru", "Russia", "Romania", "South Africa", "Spain", "Sri Lanka", "Sweden", "Switzerland",
        "Thailand", "Turkey", "Uganda", "Ukraine", "United States", "United Kingdom", "Vietnam"];

    const addCountry = (selectedCountry) => {
        options.innerHTML = "";
        countries.forEach((country) => {
            let isSelected = country === selectedCountry ? "selected" : "";
            let li = `<li onclick="updateName(this)" class="${isSelected}">${country}</li>`;
            options.insertAdjacentHTML("beforeend", li);
        });
    };

    const updateName = (selectedLi) => {
        searchInp.value = "";
        addCountry(selectedLi.innerText);
        wrapper.classList.remove("active");
        selectBtn.firstElementChild.innerText = selectedLi.innerText;
    };

    searchInp.addEventListener("keyup", () => {
        let arr = [];
        let searchWord = searchInp.value.toLowerCase();
        arr = countries
            .filter((data) => {
                return data.toLowerCase().startsWith(searchWord);
            })
            .map((data) => {
                let isSelected = data === selectBtn.firstElementChild.innerText ? "selected" : "";
                return `<li onclick="updateName(this)" class="${isSelected}">${data}</li>`;
            })
            .join("");
        options.innerHTML = arr ? arr : `<p style="margin-top: 10px;">Oops! Country not found</p>`;
    });

    selectBtn.addEventListener("click", () => wrapper.classList.toggle("active"));
    // Fim do código JavaScript fornecido

    const adicionarCompetencia = (valor) => {
        const novaCompetencia = {
            valor: valor,
            imagem: obterCaminhoImagem(valor),
        };

        setCompetencias([...competencias, novaCompetencia]);
    };

    const removerCompetencia = (index) => {
        const novasCompetencias = [...competencias];
        novasCompetencias.splice(index, 1);
        setCompetencias(novasCompetencias);
    };

    const handleAdicionarCompetencia = () => {
        let valorSelecionado = selectValue;

        adicionarCompetencia(valorSelecionado);
        setSelectValue('');
    };

    return (
        <ContainerPage>
            <main>
                <form id="form2">
                    {/* Seção de competências */}
                    <section className="competencias-usuario">
                        <div className="wrapper">
                            <div className="select-btn">
                                <span>Select Country</span>
                                <i className="uil uil-angle-down"></i>
                            </div>
                            <div className="content">
                                <div className="search">
                                    <i className="uil uil-search"></i>
                                    <input
                                        spellCheck="false"
                                        type="text"
                                        placeholder="Search"
                                        value={selectValue}
                                        onChange={(e) => setSelectValue(e.target.value)}
                                    />
                                </div>
                                <ul className="options"></ul>
                            </div>
                        </div>
                    </section>

                    {/* Lista de competências adicionadas */}
                    <div id="opcoes-container">
                        <ul id="lista-competencias" style={{ listStyleType: 'none', padding: 0 }}>
                            {competencias.map((competencia, index) => (
                                <div
                                    key={index}
                                    style={{
                                        position: 'relative',
                                        display: 'inline-block',
                                        margin: '5px',
                                        boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px',
                                        borderRadius: '16px',
                                    }}
                                >
                                    <li
                                        style={{
                                            color: 'white',
                                            padding: '5px',
                                            width: '200px',
                                            height: '200px',
                                            position: 'relative',
                                            bottom: '5px',
                                        }}
                                    >
                                        {competencia.valor}
                                        <img
                                            src={competencia.imagem}
                                            alt={`Imagem de ${competencia.valor}`}
                                            style={{ width: '150px', height: '150px', display: 'block', margin: '10px auto' }}
                                        />
                                        <button
                                            type="button"
                                            onClick={() => removerCompetencia(index)}
                                            style={{
                                                position: 'absolute',
                                                top: 0,
                                                right: 0,
                                                border: 'none',
                                                background: 'none',
                                                cursor: 'pointer',
                                            }}
                                        >
                                            <img src="../../../../multiplesign.png" alt="Remover" style={{ width: '15px', height: '15px' }} />
                                        </button>
                                    </li>
                                </div>
                            ))}
                        </ul>
                    </div>

                    <div className="box-btn-add">
                        <button type="button" id="addElementBtn" onClick={handleAdicionarCompetencia}>
                            Adicionar Competência
                            <img src="../../../plus.png" id="img-plus" alt="Plus" />
                        </button>
                    </div>

                    <div className="actions">
                        <Link to="/"> <button type="button" className="buttons" id="btnBack"><img src="../../../setaesquerda.png" alt="Seta" />Voltar</button></Link>
                        <button type="submit" className="buttons" id="btnNext" onClick={() => navigate('/Nivelamento', { state: { competencias } })}>
                            Avançar<img src="../../../setadireita.png" alt="Seta" />
                        </button>
                    </div>
                </form>
            </main>
        </ContainerPage>
    );
}

// Função para obter o caminho da imagem com base na competência
const obterCaminhoImagem = (competencia) => {
    const imagensFundo = {
        Java: '../../../../javaimg.png',
        Python: '../../../../pythonimg.webp',
        JavaScript: '../../../../javascript.png',
        React: '../../../../react.png',
        Angular: '../../../../angularimg.png',
        TypeScript: '../../../../typescriptimg.png',
        Outros: 'outros-background.jpg',
    };

    return `/img/${imagensFundo[competencia] || 'default-background.jpg'}`;
}

export default Page;
