import React, { useState } from "react";
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

import { ContainerPage } from "../../components/Main";

const Page = () => {
    const [competencias, setCompetencias] = useState([]);
    const [selectValue, setSelectValue] = useState('');
    const navigate = useNavigate();

    const adicionarCompetencia = (valor) => {
        const novaCompetencia = {
            valor: valor,
            imagem: obterCaminhoImagem(valor),
        };

        setCompetencias([...competencias, novaCompetencia]);
    };

    const removerCompetencia = (index) => {
        const novasCompetencias = [...competencias];
        novasCompetencias.splice(index, 1);
        setCompetencias(novasCompetencias);
    };

    const handleAdicionarCompetencia = () => {
        let valorSelecionado = selectValue;

        adicionarCompetencia(valorSelecionado);
        setSelectValue('');
    };

    return (
        <ContainerPage>
            <main>
                <form id="form2">
                    {/* Seção de competências */}
                    <section className="competencias-usuario">
                        <CountrySelector
                            selectValue={selectValue}
                            setSelectValue={setSelectValue}
                            addCountry={adicionarCompetencia}
                        />
                    </section>

                    {/* Lista de competências adicionadas */}
                    <div id="opcoes-container">
                        <ul id="lista-competencias" style={{ listStyleType: 'none', padding: 0 }}>
                            {competencias.map((competencia, index) => (
                                <div
                                    key={index}
                                    style={{
                                        position: 'relative',
                                        display: 'inline-block',
                                        margin: '5px',
                                        boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px',
                                        borderRadius: '16px',
                                    }}
                                >
                                    <li
                                        style={{
                                            color: 'white',
                                            padding: '5px',
                                            width: '200px',
                                            height: '200px',
                                            position: 'relative',
                                            bottom: '5px',
                                        }}
                                    >
                                        {competencia.valor}
                                        <img
                                            src={competencia.imagem}
                                            alt={`Imagem de ${competencia.valor}`}
                                            style={{ width: '150px', height: '150px', display: 'block', margin: '10px auto' }}
                                        />
                                        <button
                                            type="button"
                                            onClick={() => removerCompetencia(index)}
                                            style={{
                                                position: 'absolute',
                                                top: 0,
                                                right: 0,
                                                border: 'none',
                                                background: 'none',
                                                cursor: 'pointer',
                                            }}
                                        >
                                            <img src="../../../../multiplesign.png" alt="Remover" style={{ width: '15px', height: '15px' }} />
                                        </button>
                                    </li>
                                </div>
                            ))}
                        </ul>
                    </div>

                    <div className="box-btn-add">
                        <button type="button" id="addElementBtn" onClick={handleAdicionarCompetencia}>
                            Adicionar Competência
                            <img src="../../../plus.png" id="img-plus" alt="Plus" />
                        </button>
                    </div>

                    <div className="actions">
                        <Link to="/"> <button type="button" className="buttons" id="btnBack"><img src="../../../setaesquerda.png" alt="Seta" />Voltar</button></Link>
                        <button type="submit" className="buttons" id="btnNext" onClick={() => navigate('/Nivelamento', { state: { competencias } })}>
                            Avançar<img src="../../../setadireita.png" alt="Seta" />
                        </button>
                    </div>
                </form>
            </main>
        </ContainerPage>
    );
}

// Função para obter o caminho da imagem com base na competência
const obterCaminhoImagem = (competencia) => {
    const imagensFundo = {
        Java: '../../../../javaimg.png',
        Python: '../../../../pythonimg.webp',
        JavaScript: '../../../../javascript.png',
        React: '../../../../react.png',
        Angular: '../../../../angularimg.png',
        TypeScript: '../../../../typescriptimg.png',
        Outros: 'outros-background.jpg',
    };

    return `/img/${imagensFundo[competencia] || 'default-background.jpg'}`;
}

export default Page;

const CountrySelector = ({ selectValue, setSelectValue, addCountry }) => {
    const [isActive, setIsActive] = useState(false);

    const countries = ["Afghanistan", "Algeria", "Argentina", "Australia", /*... lista completa de países ...*/];

    const handleInputChange = (e) => {
        const value = e.target.value;
        setSelectValue(value);
    };

    const handleToggleOptions = () => {
        setIsActive(!isActive);
    };

    return (
        <div className={`wrapper ${isActive ? "active" : ""}`}>
            <div className="select-btn" onClick={handleToggleOptions}>
                <span>{selectValue || "Select Country"}</span>
                <i className="uil uil-angle-down"></i>
            </div>
            <div className="content">
                <div className="search">
                    <i className="uil uil-search"></i>
                    <input
                        spellCheck="false"
                        type="text"
                        placeholder="Search"
                        value={selectValue}
                        onChange={handleInputChange}
                    />
                </div>
                <ul className="options">
                    {countries.map((country) => (
                        <li
                            key={country}
                            onClick={() => {
                                addCountry(country);
                                setIsActive(false);
                            }}
                            className={country === selectValue ? "selected" : ""}
                        >
                            {country}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}
