import styled from 'styled-components';

export const AreaHeader = styled.div`

        .header-bar {
            background-color: #0B63CE;
            height: 65px;
            
        } 
        

        a {
            text-decoration: none;
        }
        
        ul {
            list-style: none;
        }
        
        .nav-bar {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding: .4rem 2rem;
        }

        .logo-img-search-box {
            display: flex;
            gap: 25px;

        }
        
        .search-box {
            margin-top: 2px;
            height: 45px;
            border-radius: 10px;
            background-color: #FFF;
            display: flex;
            justify-content: space-between;
            align-items: center;
        }
        
        .search-text {
            width: 400px;
            border: none;
            background: none;
            outline: none;
            font-size: 15px;
            padding-left: 10px;
        }
        
        .search-btn {
            width: 40px;
            height: 40px;
            border-radius: 50%;
            display: flex;
            justify-content: center;
            align-items: center;
        }

        .nav-items-btn-user-img {
            height: 45px;
            display:flex;
            justify-content: space-evenly;
            gap: 50px;
        }
        
        
        .nav-list {
            
            display: flex;
            list-style: none;
            gap: 50px;
        }
        
        .nav-link {
            text-decoration: none;
            padding-top: 1vh;
            font-size: 1.15rem;
            font-weight: 400;
            color: #FFF;
            position: relative;
        }
        
        
        .nav-list a:hover{
            opacity: 0.7;
        }
        
    

        
        @media only screen and (max-width: 926px) {
            
        
            .search-text {
                max-width: 100px;
            }
        
        
            .header-bar {
                
                display: flex;
                height: 95px;
            }
        
            .logo-img {
                padding-right: 10px;
        
            }

            .nav-bar {
                display: flex;
                justify-content: space-between;
                align-items: center;
                padding: 0;
            }

            .logo-img-search-box {
                gap: 0px;
    
            }

            .nav-items-btn-user-img {
                border: 2px solid red;
                height: 45px;
               
                justify-content: space-evenly;
                gap: 0px;
            }

            


`;
