import React, { useState } from "react";
import { Link } from 'react-router-dom';
import { useLocation } from "react-router-dom";

import { ContainerPage } from "../../components/Main";


const Nivelamento = () => {
  const location = useLocation();
  const competencias = location.state?.competencias || [];
  

  // Adicione um estado para armazenar os níveis selecionados
  const [nivelSelecionado, setNivelSelecionado] = useState({});
  const [finalizarClicado, setFinalizarClicado] = useState(false);

  // Função para atualizar o nível selecionado
  const handleNivelChange = (index, novoNivel) => {
    setNivelSelecionado((prev) => ({ ...prev, [index]: novoNivel }));
  };

  const handleFinalizarClick = () => {
    setFinalizarClicado(true);

    // Criar um novo objeto para representar cada competência no formato desejado
  const competenciasFormatadas = competencias.map(({ valor, imagem }) => ({
    valor,
    imagem,
  }));

    const dadosFormulario = {
      competencias,
      niveis: nivelSelecionado,
    };
    alert(JSON.stringify(dadosFormulario, null, 2));
    // Aqui você pode enviar os dados do formulário para o backend ou realizar outras ações
  };

  return (
  <ContainerPage>
    <div>
      <div style={{textAlign:'center', marginTop:'25px'}}>
        <h2>Agora para finalizar nos informe em que nível você se encontra em cada tecnologia selecionada:</h2>
      </div>
      
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent:'center', marginTop:'50px' }}>
        {competencias.map((competencia, index) => (
          <div key={index} style={{ margin: '10px', textAlign: 'center',display:'flex', flexWrap:'wrap', justifyContent:'center', width:'15%',
          margin: '5px',
          boxShadow: 'rgba(99, 99, 99, 0.2) 0px 2px 8px 0px',
          borderRadius: '16px',
          
          }}>
            <img
              src={competencia.imagem}
              alt={`Imagem de ${competencia.valor}`}
              style={{ width: '150px', height: '150px', display: 'block'}}
            />
            <select
              id="selectValue"
              className="form-select form-select-lg mb-3"
              aria-label=".form-select-lg example"
              value={nivelSelecionado[index] || ''}
              onChange={(e) => handleNivelChange(index, e.target.value)}
              style={{ marginTop: '10px', width:'65%' }}
            >
              <option value="" data-default disabled selected>Selecione...</option>
              <option value="Master">Master</option>
              <option value="Leader">Leader</option>
              <option value="Teacher">Teacher</option>
              <option value="Professional">Professional</option>
              <option value="Practitioner">Practitioner</option>
              <option value="Apprentice">Apprentice</option>
              <option value="Beginner">Beginner</option>
            </select>
          </div>
        ))}

          
        
      </div>
      <div className="actions">
          <Link to="/Competencias"> <button type="button" className="buttons" id="btnNext1">Voltar<img src="../../../seta.png" alt="Seta" /></button></Link>
          <button type="button" className="buttons" id="btnFinalizar" onClick={handleFinalizarClick}>
            Finalizar
          </button>             
      </div>
    </div>
  </ContainerPage>
  );
};

export default Nivelamento;
